import React from "react";

export const ContactForm = ({
  name,
  setName,
  phone,
  setPhone,
  email,
  setEmail,
  handleSubmit
}) => {
  return (
    <form id="contactForm" onSubmit={handleSubmit}>
      <label>
        <input type="text" name="name" value={name} onChange={(e) => setName(e.target.value)} required placeholder="Contact Name" aria-label="Contact Name"/>
      </label>
      <br />
      <label></label>
      <input type="number" name="phone" pattern="^\(?[\d]{3}\)?[\s-]?[\d]{3}[\s-]?[\d]{4}$" value={phone} onChange={(e) => setPhone(e.target.value)} />
      <input type="text" name="email" value={email} onChange={(e) => setEmail(e.target.value)} />
      <input type="submit" onSubmit={handleSubmit} />

    </form>
  );
};
